<script>
    function setFontWeight(a, b, c) { a.style.fontWeight = c, b.style.fontWeight = c } let feed = document.getElementById("feed"), newspaper = document.getElementById("newspaper"); feed.onmouseover = function () { setFontWeight(this, newspaper, "bold") }, feed.onmouseleave = function () { setFontWeight(this, newspaper, "100") }; let feed2 = document.getElementById("feed2"), user = document.getElementById("user"); feed2.onmouseover = function () { setFontWeight(this, user, "bold") }, feed2.onmouseleave = function () { setFontWeight(this, user, "100") }; let feed3 = document.getElementById("feed3"), friends = document.getElementById("friends"); feed3.onmouseover = function () { setFontWeight(this, friends, "bold") }, feed3.onmouseleave = function () { setFontWeight(this, friends, "100") }; let logou = document.getElementById("logout"), logoutimg = document.getElementById("logout-img"); logout.onmouseover = function () { setFontWeight(this, logoutimg, "bold") }, logout.onmouseleave = function () { setFontWeight(this, logoutimg, "100") };
</script>
